import React, { useState } from 'react';
import logo from '../components/Asset/xiomi.png';
import { FaBars, FaSearch, FaShoppingCart, FaUser } from 'react-icons/fa';
import './NavBar.css'; // Import your CSS file for styling

const NavBar = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [nav, setNav] = useState(false);
    
    const handleClick = () => setNav(!nav);
    const handleMouseEnter = () => setIsOpen(true);
    const handleMouseLeave = () => setIsOpen(false);
  
    return (
        <div className='container'>
            <nav className='navigation'>
                <div className={`nav-links ${nav ? 'active' : ''}`}>
                    <ul className='nav-menu'>
                        <li className='nav-item xiomi-logo'>
                            <img src={logo} alt='Xiomi Logo' className='xiomi-logo' />
                        </li>
                        <li className='nav-item'>Store</li>
                        <li className='nav-item dropdown' onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                            <button className="dropbtn">Products</button>
                            {isOpen && (
                                <div className="dropdown-content">
                                    <a href="#">Product 1</a>
                                    <a href="#">Product 2</a>
                                    <a href="#">Product 3</a>
                                </div>
                            )}
                        </li>
                        <li className='nav-item'>TV & Smart Home</li>
                        <li className='nav-item'>Tablet</li>
                        <li className='nav-item'>Lifestyle</li>
                        <div className='user'>
                            <li className='nav-item'>Discover</li>
                            <li className='nav-item'>Support</li>
                            <div className='icon'>
                                <li className='nav-item'><FaSearch /></li>
                                <li className='nav-item'><FaShoppingCart /></li>
                                <li className='nav-item'><FaUser /></li>
                                <div className="menu-icon" onClick={handleClick}>
                                    {nav ? <FaTimes /> : <FaBars />}
                                </div>
                            </div>
                        </div>
                    </ul>
                </div>
            </nav>
        </div>
    );
}

export default NavBar;
