import React, { useState } from 'react';
import logo from '../components/Asset/xiomi.png';
import { FaTimes, FaBars, FaSearch, FaShoppingCart, FaUser } from 'react-icons/fa';


 export const NavBar = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [nav, setNav] = useState(false);
    
    const handleClick = () => setNav(!nav);
    const handleMouseEnter = () => setIsOpen(true);
    const handleMouseLeave = () => setIsOpen(false);
  
    return (
        
            <nav className='navigation'>
                <div className={`nav-links ${nav ? 'active' : ''}`}>
                    <ul className='nav-menu'>
                        <li className='nav-item xiomi-logo'>
                            <img src={logo} alt='Xiomi Logo' className='xiomi-logo' />
                        </li>
                       
                        <li className={`nav-item ${nav ? 'active' : ''}`}>Store</li>
                        <li className={`nav-item dropdown ${nav ? 'active' : ''}`} onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
                            <button className="dropbtn">Products</button>
                            {isOpen && (
                                <div className="dropdown-content" style={{width:'100%'}}>
                                    <a href="#">Product 1</a>
                                    <a href="#">Product 2</a>
                                    <a href="#">Product 3</a>
                                </div>
                            )}
                        </li>
                        <li className={`nav-item ${nav ? 'active' : ''}`}>TV & Smart Home</li>
                        <li className={`nav-item ${nav ? 'active' : ''}`}>Tablet</li>
                        <li className={`nav-item ${nav ? 'active' : ''}`}>Lifestyle</li>
                        <div className={`user ${nav ? 'active' : ''}`}>
                            <li className={`nav-item ${nav ? 'active' : ''}`}>Discover</li>
                            <li className={`nav-item ${nav ? 'active' : ''}`}>Support</li>
                            <div className='icon'>
                                <li className={`nav-item ${nav ? 'active' : ''}`}><FaSearch /></li>
                                <li className={`nav-item ${nav ? 'active' : ''}`}><FaShoppingCart /></li>
                                <li className={`nav-item ${nav ? 'active' : ''}`}><FaUser /></li>
                            </div>
                        </div>
                    </ul>
                    
                </div>
            </nav>
       
    );
}

export default NavBar;
