import React , { useState } from 'react';
import logo from '../components/Asset/xiomi.png';
import { FaSearch,FaShoppingCart,FaUser } from 'react-icons/fa';
 export const NavBar = () => {





   
        const [isOpen, setIsOpen] = useState(false);
      
        const handleMouseEnter = () => {
          setIsOpen(true);
        };
      
        const handleMouseLeave = () => {
          setIsOpen(false);
        };
  return (
    <div className='container'>
      <nav className='navigation'>
        <ul className='nav-menu'>
          <li className='nav-item xiomi-logo'>
            <img src={logo} alt='Xiomi Logo'  className='xiomi-logo'/>
          </li>
          <li className='nav-item'>Store</li>
          <li className='nav-item'>
            <div className="dropdown" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
             <button className="dropbtn">Dropdown</button>
                 {isOpen && (
                      <div className="dropdown-content">
          <a href="#">Link 1</a>
          <a href="#">Link 2</a>
          <a href="#">Link 3</a>
        </div>
        )}
        </div>
        </li>
          <li className='nav-item'>TV & Smart Home</li>
          <li className='nav-item'>Tablet</li>
          <li className='nav-item'>Lifestyle</li>
          <div className='user'>
          <li className='nav-item'>Discover</li>
          <li className='nav-item'>Support</li>
          <div className='icon'>
          <li className='nav-item'><FaSearch/></li>
          <li className='nav-item'><FaShoppingCart /></li>
          <li className='nav-item'><FaUser/></li>
          </div>
          </div>
        </ul>
      </nav>
    </div>
  );
}
